content:
.input-field {
  position: relative;
  transition: all 0.2s;
   min-height: 40px;
   border-radius: 5px;
  max-height: 80px;
}

.input-field input[type="text"],
.input-field input[type="email"],
.input-field input[type="tel"],
.input-field input[type="number"],
.input-field input[type="date"],
.input-field input[type="search"],
.input-field input[type="password"],
.input-field input[type="time"],
.input-field input[type="number"],
.input-field textarea {
  width: 100% !important;
  height: 40px !important;
  cursor: pointer;
  position: relative;
  background: rgba(245, 245, 245, 0);
  transition: all 0.2s;
  border: 1px solid rgb(173, 173, 173);
  border-radius: 5px;
  padding: 0px 10px 0px !important;
  min-height: 40px;
  z-index: 2;
}

.input-field input[type="text"] + label,
.input-field input[type="email"] + label,
.input-field input[type="tel"] + label,
.input-field input[type="number"] + label,
.input-field input[type="date"] + label,
.input-field input[type="search"] + label,
.input-field input[type="password"] + label,
.input-field input[type="time"] + label,
.input-field input[type="number"] + label,
.input-field textarea + label {
  position: absolute !important;
  top: 50%;
  transform: translateY(-50%);
  left: 10px;
  transition: all 0.2s;
  z-index: 1;
  font-style: italic;
  font-size: 14px;
  background: #193756;
  color: #51C9CD;
  white-space: nowrap;
  padding: 2px 3px;
 border-radius: 4px;
  line-height: 1;
}

.input-field input::placeholder {
  opacity: 0;
}

.input-field input:hover {
  border: 1px solid #007bff;
}


.input-field input:focus,
.input-field input:valid {
  border: 1px solid #007bff !important;
}

.input-field textarea {
  height: 100%;
  min-height: 80px;
  padding: 10px;
  resize: none;
}

.input-field textarea + label {
  transform: translateY(-150%);
}

.input-field input[type="text"]:focus + label,
.input-field input[type="email"]:focus + label,
.input-field input[type="number"]:focus + label,
.input-field input[type="date"]:focus + label,
.input-field input[type="password"]:focus + label,
.input-field input[type="time"]:focus + label,
.input-field input[type="search"]:focus + label,
.input-field input[type="number"]:focus + label,
.input-field input[type="tel"]:focus + label,
.input-field input[type="text"]:valid + label,
.input-field input[type="email"]:valid + label,
.input-field input[type="number"]:valid + label,
.input-field input[type="date"]:valid + label,
.input-field input[type="password"]:valid + label,
.input-field input[type="time"]:valid + label,
.input-field input[type="search"]:valid + label,
.input-field input[type="number"]:valid + label,
.input-field input[type="tel"]:valid + label {
  top: 0%;
  transform: translateY(-50%);
  z-index: 5 !important;
  left: 5px !important;
  background: #193756 !important;
  color: #51C9CD;
  font-size: 12px !important;
  white-space: nowrap;
  padding: 2px 3px !important;
  line-height: 1;
  border-radius: 4px;
  pointer-events: none;
}

.input-field textarea:focus + label,
.input-field textarea:valid + label {
  font-size: 12px !important;
  top: 0%;
  border-radius: 5px;
  transform: translateY(-50%) !important;
  z-index: 5 !important;
  left: 5px !important;
  background: #193756 !important;
  color: #51C9CD;
  white-space: nowrap;
  padding: 2px 3px !important;
  line-height: 1;
  border-radius: 4px;
  pointer-events: none;
}

.input-field input[type="number"] {
  -moz-appearance: textfield;
}

.input-field input[type="number"]::-webkit-outer-spin-button,
.input-field input[type="number"]::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}

.no-search-cancel::-webkit-search-cancel-button {
  -webkit-appearance: none;
  appearance: none;
  display: none;
}
