content:
* {
  padding: 0px;
  margin: 0;
  border: 0px;
  box-sizing: border-box;
}

*:before,
*:after {
  box-sizing: border-box;
}

:focus,
:active {
  outline: none;
}

a:focus,
a:active {
  outline: none;
}

aside,
nav,
footer,
header,
section {
  display: block;
}

html {
  max-width: 100vw;
}

body {
  max-width: 100vw;
  text-size-adjust: 100%;
}

input,
button,
textarea {
  resize: none;
}

input {
  margin: 0 0 0 0;
  padding: 0 0 0 0;
}

input::-ms-clear {
  display: none;
}

button {
  cursor: pointer;
}

button::-moz-focus-inner {
  padding: 0;
  border: 0px;
}

h1,h2,h3,h4,h5,h6 {
  display: block;
}

a {
  text-decoration: none !important;
}

a:active,
a:hover {
  outline: 0;
}

a:visited {
  text-decoration: none !important;
}

img {
  vertical-align: top;
}

ul,
li,
ol {
  list-style-type: none;
  margin: 0;
  padding: 0;
}

img,
audio,
video {
  height: auto;
}

audio,
canvas,
iframe,
video,
img,
svg {
  vertical-align: middle;
}

iframe {
  border: 0;
}

textarea {
  resize: none;
  overflow: auto;
  vertical-align: top;
  box-shadow: none;
  -webkit-box-shadow: none;
  -moz-box-shadow: none;
}

input,
textarea,
select,
button {
  outline: none;
  border: none;
  font-size: 100%;
  margin: 0;
}

button,
input {
  line-height: normal;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

td,
th {
  padding: 0;
  text-align: left;
}

.container {
  max-width: 1200px;
  width: 100%;
  margin: 0 auto;
  position: relative;
}

*:has(> .imgs) {
  position: relative;
}

.imgs {
  overflow: hidden;
  position: relative;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}

.imgs img {
  height: 100%;
  width: 100%;
  object-fit: cover;
  object-position: top;
  position: absolute;
  top: 0;
  left: 0;
}

@media (min-width: 901px) and (max-width: 1259px) {
  .container {
    margin: 0 20px;
    max-width: calc(100% - 40px);
  }
}

@media (max-width: 900px) {
  .container {
    margin: 0 10px;
    max-width: calc(100% - 20px);
  }
}

a {
  display: inline-block;
  transition: all 0.2s ease-in-out;
  cursor: pointer;
  color: rgb(72, 89, 245);
}

a:hover {
  color: rgb(9, 72, 207);
  text-shadow: 0 0 6px #053fa1;
}

img {
  width: 100%;
  height: 100%;
}
 svg {
 width: 20px ;
 height: 20px ;
 }
